function FFTinterpfilter=FFTfractinterpfilter(M,alpha,tau,type)%% 	Author: %     Dimitri Van De Ville%% 	Biomedical Imaging Group, EPFL, Lausanne, Switzerland.% 	This software is downloadable at http://bigwww.epfl.ch/%if real(alpha)==0 & tau==0.5,    FFTinterpfilter=ones(1,M);    return;end;if real(alpha)<=0.5	disp('The interpolation prefilter of the fractional splines exists only ')	disp('for degrees strictly larger than 0.5!')	FFTinterpfilter=[];	returnendnu=0:1/M:(1-1/M);FFTinterpfilter=fractsplineautocorr(alpha,tau,nu);